mapper.select:
select
id, user_id, project_id, link_sys_custom_field, values, create_time, update_time
from sys_custom_field_expand


        and user_id = #{userId}


        and project_id = #{projectId}


        and link_sys_custom_field = #{linkSysCustomField}


        and values = #{values}


        and create_time = #{createTime}


        and update_time = #{updateTime}
