mapper.select:
select id,
title
from test_cycle

    project_id = #{projectId}
    and user_id = #{masterId}

        and title like concat('%',#{title},'%')

select
id, user_id, project_id, title, status, run_status, last_run_date, last_modify,description, version,
create_time, update_time, ped, author_name,assign_to,notifiy_list
from oneclick.test_cycle


        and id = #{id}


        and user_id = #{userId}


        and project_id = #{projectId}


        and title = #{title}


        and status = #{status}


        and run_status = #{runStatus}


        and last_run_date = #{lastRunDate}


        and last_modify = #{lastModify}



        and version = #{version}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and ped = #{ped}


        and author_name = #{authorName}

SELECT version
FROM test_cycle
WHERE project_id = #{projectId}
AND env = #{env}
AND version = #{version}

SELECT *
FROM test_case ca
         JOIN test_cycle_join_test_case cy ON ca.id = cy.test_case_id
         JOIN test_cycle tc on cy.test_cycle_id = tc.id WHERE ca.project_id = #{param1}
    AND ca.version = #{param2}
    and ca.env = #{param3}
    and tc.version in (#{param4})
